<?phpclass ControllerToolC6V extends Controller {	public function getCities() {		$this->load->model('extension/shipping/c6v');		$data = $this->model_extension_shipping_c6v->getCities();		$this->response->addHeader('Content-Type: application/json');		$this->response->setOutput(json_encode($data));	}	public function setPickupCity() {		if (isset($this->request->post['pickup_city'])) {			$this->session->data['c6v_pickup_city'] = $this->request->post['pickup_city'];		}	}}